INSERT INTO `bb_posts` (`post_id`, `topic_id`, `forum_id`, `poster_id`, `icon_id`, `poster_ip`, `post_time`, `post_approved`, `post_reported`, `enable_bbcode`, `enable_smilies`, `enable_magic_url`, `enable_sig`, `post_username`, `post_subject`, `post_text`, `post_checksum`, `post_attachment`, `bbcode_bitfield`, `bbcode_uid`, `post_postcount`, `post_edit_time`, `post_edit_reason`, `post_edit_user`, `post_edit_count`, `post_edit_locked`, `post_wpu_xpost_parent`, `post_wpu_xpost_meta1`, `post_wpu_xpost_meta2`) VALUES
(1, 1, 2, 2, 0, '127.0.0.1', 1373902010, 1, 0, 1, 1, 1, 1, '', 'Vítejte v&nbsp;phpBB3', 'Toto je příklad toho, jak bude vypadat příspěvek ve vaší instalaci phpBB3. Vypadá to, že vše běží v&nbsp;pořádku, tak jak má. Můžete smazat tento příspěvek a pokračovat v&nbsp;nastavování vašeho nového fóra. Během instalace byla první kategorii a fóru nastavena sada oprávnění pro všechny předdefinované skupiny: Administrátory, Globální Moderátory, Registrované uživatele, Anonymní návštěvníky, Boty a případné registrované COPPA uživatele. Všechna nově vytvořená fóra nebudou mít nastavena na začátku žádná oprávnění, doporučujeme vám využít těchto, již nastavených, oprávnění a při tvorbě nových fór je pouze zkopírovat pomocí stejnojmenné funkce. Doufáme, že vám vaše nové fórum přinese mnoho zábavy a užitku!', '5dd683b17f641daf84c040bfefc58ce9', 0, '', '', 1, 0, '', 0, 0, 0, NULL, NULL, NULL);
