INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2013-07-15 15:30:08', '2013-07-15 15:30:08', 'Vítejte ve WordPressu. Toto je váš první testovací příspěvek. Můžete ho upravit, nebo smazat a postupně pak začít s tvorbou vlastního webu.', 'Ahoj všichni!', '', 'publish', 'open', 'open', '', 'ahoj-vsichni', '', '', '2013-07-15 15:30:08', '2013-07-15 15:30:08', '', 0, 'http://localhost:8080/projects/vch/wordpress/?p=1', 0, 'post', '', 1),
(2, 1, '2013-07-15 15:30:08', '2013-07-15 15:30:08', 'Toto je první ukázková stránka, která byla automaticky vytvořena během instalace WordPressu, a je možné ji jednoduše upravovat a doplnit třeba některé osobní informace (např. odkud pocházíte, co děláte), které by mohly zajímat vaše čtenáře. Můžete také vytvářet další podobné stránky a jejich podstránky, čímž vlastně začnete vytvářet a spravovat obsah webu.', 'Něco o mně', '', 'publish', 'open', 'open', '', 'neco-o-me', '', '', '2013-07-15 15:30:08', '2013-07-15 15:30:08', '', 0, 'http://localhost:8080/projects/vch/wordpress/?page_id=2', 0, 'page', '', 0),
(3, 1, '2013-07-15 15:30:37', '0000-00-00 00:00:00', '', 'Automaticky vytvořený koncept', '', 'auto-draft', 'open', 'open', '', '', '', '', '2013-07-15 15:30:37', '0000-00-00 00:00:00', '', 0, 'http://localhost:8080/projects/vch/wordpress/?p=3', 0, 'post', '', 0),
(4, 0, '2013-07-15 17:46:47', '2013-07-15 17:46:47', '<!--wp-united-phpbb-forum-->', 'Forum', '', 'publish', 'closed', 'closed', '', 'forum', '', '', '2013-07-15 17:46:47', '2013-07-15 17:46:47', '', 0, '', 0, 'page', '', 0);
